mapper.update:
UPDATE chat_room_members
SET last_read_message_id = #{messageId}
WHERE room_id = #{roomId}
  AND user_id = #{userId}

UPDATE chat_room_members
SET last_read_message_id = #{messageId}
WHERE room_id = #{roomId}
  AND user_id = #{userId}
  AND (last_read_message_id IS NULL OR last_read_message_id < #{messageId})

mapper.select:
SELECT id, type, name, creator_id, created_at, updated_at
FROM chat_rooms
WHERE id = #{roomId}

mapper.update:
UPDATE chat_room_members
SET left_at = now()
WHERE room_id = #{roomId}
  AND user_id = #{userId}
  AND left_at IS NULL

mapper.select:
SELECT EXISTS(
    SELECT 1 FROM chat_room_members
    WHERE room_id = #{roomId}
      AND user_id = #{userId}
      AND left_at IS NULL
)

mapper.update:
UPDATE chat_room_members
SET left_at = now()
WHERE room_id = #{roomId}
  AND user_id = #{userId}
  AND left_at IS NULL

mapper.select:
SELECT COUNT(*)
FROM messages m
WHERE m.chat_room_id = #{roomId}
  AND m.id > COALESCE(
      (SELECT last_read_message_id
       FROM chat_room_members
       WHERE room_id = #{roomId} AND user_id = #{userId}),
      0
  )
  AND m.sender_id != #{userId}
