mapper.insert:
INSERT INTO files (
    uploader_id, target_domain, target_id, purpose, file_url, created_at
) VALUES (
    #{uploaderId}, #{targetDomain}, #{targetId}, #{purpose}::file_purpose, #{fileUrl}, NOW()
)

mapper.select:
SELECT * FROM files
WHERE target_domain = #{targetDomain}
  AND target_id = #{targetId}
  AND deleted_at IS NULL
ORDER BY created_at ASC

SELECT * FROM files
WHERE target_domain = #{targetDomain}
  AND target_id IN

      #{id}

  AND deleted_at IS NULL
