mapper.insert:
INSERT INTO reports (
    reporter_id, target_type, target_id, reason, description, status, created_at, updated_at
) VALUES (
    #{reporterId}, #{targetType}::report_target_type, #{targetId}, #{reason}::report_reason_type, 
    #{description}, #{status}::report_status, NOW(), NOW()
)

mapper.select:
SELECT EXISTS(
    SELECT 1 FROM reports
    WHERE reporter_id = #{reporterId}
      AND target_type = #{targetType}::report_target_type
      AND target_id = #{targetId}
)
