mapper.select:
SELECT EXISTS(
    SELECT 1 FROM time_tables
    WHERE user_id = #{userId} AND semester_id = #{semesterId}
)

SELECT * FROM time_tables 
WHERE user_id = #{userId}
ORDER BY created_at DESC

mapper.update:
UPDATE time_tables
SET title = #{title},
    is_public = #{isPublic}
WHERE id = #{id}
